DROP PROCEDURE IF EXISTS `stp_SIS_IDIOMA_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_IDIOMA_OBTERPORFILTRO`(IN `DESCRICAO` varchar(120), IN `SIGLA` varchar(10))
BEGIN
	#Routine body goes here...
SET @FILTRO := CONCAT('%',LOWER(TRIM(DESCRICAO)),'%');

IF(SIGLA IS NOT NULL AND SIGLA='-1') THEN
  SELECT * FROM SIS_IDIOMA WHERE (IDIO_SIGLA = SIGLA OR IDIO_SIGLA="" OR IDIO_SIGLA IS NULL) AND IDIO_STATUS='1'
  order by IDIO_DESCRICAO; 
ELSE IF(SIGLA IS NOT NULL AND SIGLA<>'-1' AND SIGLA<>'') THEN
  SELECT * FROM SIS_IDIOMA WHERE IDIO_SIGLA = SIGLA AND IDIO_STATUS='1'
  order by IDIO_DESCRICAO;
ELSE
  SELECT * FROM SIS_IDIOMA WHERE IDIO_DESCRICAO 
	LIKE @FILTRO AND IDIO_STATUS=1 order by IDIO_DESCRICAO;
END IF;
END IF;
END
;;
