-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SIS_PERFIL_FILIAL_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_PERFIL_FILIAL_OBTERPORFILTRO`(IN `PERFIL` int,IN `FILIAL` int)
BEGIN
	#Routine body goes here...
IF(PERFIL > 0 AND FILIAL >0) THEN
	SELECT F.FIL_CODIGO_EMPRESA, F.FIL_CODIGO_PERFIL, P.PER_NOME, EMP_RAZAO_SOCIAL, EMP_NOME_FANTASIA, E.FIL_CATEGORIA   FROM SIS_PERFIL_FILIAL F
	INNER JOIN SIS_EMPRESA E ON E.FIL_CODIGO = F.FIL_CODIGO_EMPRESA
	INNER JOIN SIS_PERFIL P ON P.PER_CODIGO = F.FIL_CODIGO_PERFIL
	INNER JOIN GER_ENTIDADE_EMPRESA ON EMP_CODIGO = F.FIL_CODIGO_EMPRESA
	WHERE FIL_CODIGO_EMPRESA=FILIAL AND FIL_CODIGO_PERFIL=PERFIL;
ELSE IF(PERFIL >0 AND FILIAL <=0) THEN
	SELECT F.FIL_CODIGO_EMPRESA, F.FIL_CODIGO_PERFIL, P.PER_NOME, EMP_RAZAO_SOCIAL, EMP_NOME_FANTASIA, E.FIL_CATEGORIA   FROM SIS_PERFIL_FILIAL F
	INNER JOIN SIS_EMPRESA E ON E.FIL_CODIGO = F.FIL_CODIGO_EMPRESA
