CREATE PROCEDURE `stp_SIS_UTILIZADOR_ADICIONAR`(IN ENTITY_ID INT, IN UTILIZADOR VARCHAR(20),  IN SENHA VARCHAR(200),
	ESTADO VARCHAR(1),
  EMAIL VARCHAR(120), PERFIL INT, SUPERVISOR INT, USER_CREATOR VARCHAR(20), OUT `LSTID` INT)
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.

    -- Insert statements for procedure here
 
 
IF(ESTADO!='A') THEN
  SET SENHA = NULL;
END IF;

IF (NOT EXISTS(SELECT * FROM SIS_UTILIZADOR WHERE UTI_ENTITY_ID = ENTITY_ID)) THEN
	INSERT INTO SIS_UTILIZADOR  
  (UTI_ENTITY_ID, UTI_UTILIZADOR, UTI_SENHA, UTI_ESTADO, UTI_EMAIL, UTI_CODIGO_PERFIL, UTI_SUPERVISOR, uti_created_date,
