  VALUES  (ENTITY_ID, UTILIZADOR, SENHA, ESTADO, EMAIL, PERFIL, SUPERVISOR, NOW(), USER_CREATOR);
	SELECT LAST_INSERT_ID() INTO LSTID;
ELSE

	IF(SENHA = NULL) THEN
  UPDATE SIS_UTILIZADOR SET 
	UTI_SENHA = SENHA, UTI_ESTADO = ESTADO,
	UTI_EMAIL = EMAIL, UTI_CODIGO_PERFIL=PERFIL,
  UTI_SUPERVISOR = SUPERVISOR WHERE UTI_UTILIZADOR = UTILIZADOR;
	SET LSTID = ENTITY_ID;  
  ELSE
  UPDATE SIS_UTILIZADOR SET 
	UTI_ESTADO = ESTADO,
	UTI_EMAIL = EMAIL, UTI_CODIGO_PERFIL=PERFIL,
  UTI_SUPERVISOR = SUPERVISOR WHERE UTI_UTILIZADOR = UTILIZADOR;
	SET LSTID = ENTITY_ID;
  END IF;

END IF;
IF(PERFIL !=1) THEN
  IF(NOT EXISTS(SELECT * FROM COM_DOCUMENTO_UTILIZADOR_ACESSO WHERE DOC_ACE_UTILIZADOR = UTILIZADOR))THEN
   INSERT INTO COM_DOCUMENTO_UTILIZADOR_ACESSO (DOC_ACE_CODIGO_DOCUMENTO, DOC_ACE_CODIGO_PERFIL, DOC_ACE_UTILIZADOR, DOC_ACE_INSERT, DOC_ACE_UPDATE,
   DOC_ACE_DELETE, DOC_ACE_SELECT)
   SELECT DOC_CODIGO, NULL, UTILIZADOR, 0, 0, 0, 0 FROM COM_DOCUMENTO;
	END IF;

	IF(NOT EXISTS(SELECT * FROM COM_DOCUMENTO_UTILIZADOR_ACESSO WHERE DOC_ACE_CODIGO_PERFIL = PERFIL))THEN
		 INSERT INTO COM_DOCUMENTO_UTILIZADOR_ACESSO (DOC_ACE_CODIGO_DOCUMENTO, DOC_ACE_CODIGO_PERFIL, DOC_ACE_UTILIZADOR, DOC_ACE_INSERT, DOC_ACE_UPDATE,
   DOC_ACE_DELETE, DOC_ACE_SELECT)
   SELECT DOC_CODIGO, PERFIL, NULL, 0, 0, 0, 0 FROM COM_DOCUMENTO;
	END IF; 
ELSE
   INSERT INTO COM_DOCUMENTO_UTILIZADOR_ACESSO (DOC_ACE_CODIGO_DOCUMENTO, DOC_ACE_CODIGO_PERFIL, DOC_ACE_UTILIZADOR, DOC_ACE_INSERT, DOC_ACE_UPDATE,
   DOC_ACE_DELETE, DOC_ACE_SELECT)
   SELECT DOC_CODIGO, PERFIL, NULL, 1, 1, 1, 1 FROM COM_DOCUMENTO;
END IF;
END
