        FROM SIS_UTILIZADOR_FILIAL 
        WHERE UTI_FILIAL_UTILIZADOR=SESSAO;

        SELECT u.*, FILIAL FROM SIS_UTILIZADOR U
		    WHERE U.UTI_NOME LIKE CONCAT('%',NOME,'%')
		    AND U.UTI_UTILIZADOR LIKE CONCAT('%',UTILIZADOR,'%')
        AND U.UTI_UTILIZADOR !='administrador'
