       (
          SELECT UTI_FILIAL_UTILIZADOR FROM SIS_UTILIZADOR_FILIAL 
          WHERE UTI_FILIAL_CODIGO_FILIAL = FILIAL
        )

		ORDER BY U.UTI_NOME;
