delimiter ;;
CREATE PROCEDURE `stp_SIS_UTILIZADOR_PERMISSAO_CORRIGIR`(UTILIZADOR VARCHAR(50),
TAG INT)
BEGIN
DECLARE v_CONTADOR INT;
DECLARE v_INDICE INT;
DECLARE v_FORMULARIO INT;


SELECT COUNT(FORM_CODIGO) AS v_CONTADOR FROM SIS_FORMULARIO F
INNER JOIN SIS_UTILIZADOR_PERMISSAO_FORMULARIO UPF ON UPF.UTI_PERM_FORM_CODIGO_FORMULARIO = F.FORM_CODIGO
INNER JOIN SIS_UTILIZADOR U ON U.UTI_UTILIZADOR = UPF.UTI_PERM_FORM_UTILIZADOR
WHERE UPF.UTI_PERM_FORM_ACESSO=1 AND UPF.UTI_PERM_FORM_UTILIZADOR=UTILIZADOR AND FORM_TAG=TAG;


IF(v_CONTADOR=1)
THEN

SELECT F.FORM_CODIGO, F.FORM_INDICE INTO v_FORMULARIO, v_INDICE FROM SIS_FORMULARIO F
INNER JOIN SIS_UTILIZADOR_PERMISSAO_FORMULARIO UPF ON UPF.UTI_PERM_FORM_CODIGO_FORMULARIO = F.FORM_CODIGO
INNER JOIN SIS_UTILIZADOR U ON U.UTI_UTILIZADOR = UPF.UTI_PERM_FORM_UTILIZADOR
WHERE UPF.UTI_PERM_FORM_ACESSO=1 AND UPF.UTI_PERM_FORM_UTILIZADOR=UTILIZADOR AND FORM_TAG=TAG;
IF(v_INDICE = 0)
THEN
UPDATE SIS_UTILIZADOR_PERMISSAO_FORMULARIO SET UTI_PERM_FORM_ACESSO=0
WHERE UTI_PERM_FORM_CODIGO_FORMULARIO=v_FORMULARIO AND UTI_PERM_FORM_UTILIZADOR=UTILIZADOR;
END IF;

END IF;

END
;;
