BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
DECLARE PERFIL INT DEFAULT 0;

SELECT UTI_CODIGO_PERFIL INTO PERFIL FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;
    -- Insert statements for procedure here
	SELECT DISTINCT  MOD_CODIGO, MOD_ABREVIACAO FROM SIS_MODULO WHERE MOD_CODIGO
  IN(  SELECT DISTINCT M.MOD_CODIGO FROM SIS_UTILIZADOR_PERMISSAO_FORMULARIO P
  INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = P.UTI_PERM_FORM_CODIGO_FORMULARIO
	INNER JOIN SIS_UTILIZADOR U ON U.UTI_UTILIZADOR = P.UTI_PERM_FORM_UTILIZADOR
	INNER JOIN SIS_MODULO M ON M.MOD_CODIGO = F.FORM_CODIGO_MODULO WHERE P.UTI_PERM_FORM_UTILIZADOR=UTILIZADOR
	AND P.UTI_PERM_FORM_ACESSO=1 AND M.MOD_STATUS=1)
  OR MOD_CODIGO IN ( 
  SELECT  DISTINCT MOD_CODIGO FROM SIS_MODULO_PERMISSAO 
  INNER JOIN SIS_PERFIL ON PER_CODIGO = MOD_PERM_CODIGO_PERFIL
