-- ----------------------------
-- Procedure structure for stp_SIS_UTILIZADOR_PERMISSAO_UTILIZADOR_ACESSO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_SIS_UTILIZADOR_PERMISSAO_UTILIZADOR_ACESSO`;
delimiter ;;
CREATE PROCEDURE `stp_SIS_UTILIZADOR_PERMISSAO_UTILIZADOR_ACESSO`(UTILIZADOR VARCHAR(20),
MODULO INT)
BEGIN


DECLARE PERFIL INT DEFAULT 0;
SELECT UTI_CODIGO_PERFIL INTO PERFIL FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;
IF(UTILIZADOR='administrador') THEN
SELECT 
       `FORM_PERM_CODIGO_FORMULARIO` AS UTI_PERM_FORM_CODIGO_FORMULARIO
      ,`FORM_PERM_INCLUSAO` AS UTI_PERM_FORM_INSERIR
      ,`FORM_PERM_ALTERACAO` AS UTI_PERM_FORM_ALTERAR
      ,`FORM_PERM_EXCLUSAO` AS UTI_PERM_FORM_EXCLUIR
      ,`FORM_PERM_CONSULTA` AS UTI_PERM_FORM_CONSULTAR
      ,`FORM_PERM_IMPRESSAO` AS UTI_PERM_FORM_IMPRIMIR
      ,`FORM_PERM_ACESSO` AS UTI_PERM_FORM_ACESSO,
      F.FORM_TAG, F.FORM_INDICE, FORM_TITULO, FORM_LINK, FORM_CODIGO_MODULO, FORM_IMAGEM
  FROM SIS_FORMULARIO_PERMISSAO
  INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = FORM_PERM_CODIGO_FORMULARIO 
  WHERE FORM_PERM_CODIGO_PERFIL=PERFIL
  UNION
  SELECT  
       `UTI_PERM_FORM_CODIGO_FORMULARIO`
      ,`UTI_PERM_FORM_ACESSO` 
      ,`UTI_PERM_FORM_INSERIR` 
      ,`UTI_PERM_FORM_ALTERAR` 
      ,`UTI_PERM_FORM_EXCLUIR` 
      ,`UTI_PERM_FORM_IMPRIMIR` 
      ,`UTI_PERM_FORM_CONSULTAR`, 
      F.FORM_TAG, F.FORM_INDICE, FORM_TITULO, FORM_LINK, FORM_CODIGO_MODULO, FORM_IMAGEM
      FROM SIS_UTILIZADOR_PERMISSAO_FORMULARIO 
      INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = UTI_PERM_FORM_CODIGO_FORMULARIO
      
      WHERE UTI_PERM_FORM_ACESSO = 1 AND UTI_PERM_FORM_UTILIZADOR =UTILIZADOR 
      

       ORDER BY FORM_TAG, FORM_INDICE;
ELSE
SELECT 
       `FORM_PERM_CODIGO_FORMULARIO` AS UTI_PERM_FORM_CODIGO_FORMULARIO
      ,`FORM_PERM_INCLUSAO` AS UTI_PERM_FORM_INSERIR
      ,`FORM_PERM_ALTERACAO` AS UTI_PERM_FORM_ALTERAR
      ,`FORM_PERM_EXCLUSAO` AS UTI_PERM_FORM_EXCLUIR
      ,`FORM_PERM_CONSULTA` AS UTI_PERM_FORM_CONSULTAR
      ,`FORM_PERM_IMPRESSAO` AS UTI_PERM_FORM_IMPRIMIR
      ,`FORM_PERM_ACESSO` AS UTI_PERM_FORM_ACESSO,
      F.FORM_TAG, F.FORM_INDICE, FORM_TITULO, FORM_LINK, FORM_CODIGO_MODULO, FORM_IMAGEM
  FROM SIS_FORMULARIO_PERMISSAO
  INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = FORM_PERM_CODIGO_FORMULARIO
  WHERE FORM_PERM_CODIGO_PERFIL=PERFIL AND FORM_PERM_ACESSO=1 
  UNION
  SELECT  
       `UTI_PERM_FORM_CODIGO_FORMULARIO`
      ,`UTI_PERM_FORM_ACESSO` 
      ,`UTI_PERM_FORM_INSERIR` 
      ,`UTI_PERM_FORM_ALTERAR` 
      ,`UTI_PERM_FORM_EXCLUIR` 
      ,`UTI_PERM_FORM_IMPRIMIR` 
      ,`UTI_PERM_FORM_CONSULTAR`, 
      F.FORM_TAG, F.FORM_INDICE, FORM_TITULO, FORM_LINK, FORM_CODIGO_MODULO, FORM_IMAGEM
      FROM SIS_UTILIZADOR_PERMISSAO_FORMULARIO 
      INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = UTI_PERM_FORM_CODIGO_FORMULARIO
      WHERE UTI_PERM_FORM_ACESSO = 1 AND UTI_PERM_FORM_UTILIZADOR =UTILIZADOR 
          
       ORDER BY FORM_TAG, FORM_INDICE;
END IF;
END
;;
delimiter ;

