delimiter ;;
CREATE PROCEDURE `stp_SIS_UTILIZADOR_PERMISSAO_UTILIZADOR_MODULOS`(UTILIZADOR VARCHAR(20),
PERFIL INT)
BEGIN
SELECT M.MOD_CODIGO, M.MOD_DESCRICAO, M.MOD_LINK FROM SIS_FORMULARIO_PERMISSAO P
INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = P.FORM_PERM_CODIGO_FORMULARIO
INNER JOIN SIS_MODULO M ON M.MOD_CODIGO = F.FORM_CODIGO_MODULO
INNER JOIN SIS_PERFIL PE ON PE.PER_CODIGO = P.FORM_PERM_CODIGO_PERFIL
WHERE P.FORM_PERM_CODIGO_PERFIL=PERFIL AND P.FORM_PERM_ACESSO=1 AND M.MOD_STATUS=1
UNION

SELECT M.MOD_CODIGO, M.MOD_DESCRICAO, M.MOD_LINK FROM SIS_UTILIZADOR_PERMISSAO_FORMULARIO P
INNER JOIN SIS_FORMULARIO F ON F.FORM_CODIGO = P.UTI_PERM_FORM_CODIGO_FORMULARIO
INNER JOIN SIS_UTILIZADOR U ON U.UTI_UTILIZADOR = P.UTI_PERM_FORM_UTILIZADOR
INNER JOIN SIS_MODULO M ON M.MOD_CODIGO = F.FORM_CODIGO_MODULO 
WHERE P.UTI_PERM_FORM_UTILIZADOR=UTILIZADOR AND P.UTI_PERM_FORM_ACESSO=1 AND M.MOD_STATUS=1;
 
END
;;
