delimiter ;;
CREATE PROCEDURE `stp_STOCK_EXTRATO_ARTIGO`(IN `ARTIGO_ID` int,IN `DATA_INI` date,IN `DATA_TERM` date,IN `DOCUMENT_ID` int,IN `WAREHOUSE_ID` int, IN FILIAL INT)
BEGIN
	#Routine body goes here...
 
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA = CONCAT("
SELECT DISTINCT MOV_STK_ART_CODIGO_MOVIMENTO, ART_CODIGO, MOV_STK_ART_CREATED_DATE, A.ARM_DESCRICAO, ART_DESIGNACAO, CASE WHEN DOC_STOCK = 'E' THEN 1
ELSE 0 END 
AS STOCK_TRANSACTION, MOV_STK_ART_PRECO_UNITARIO, MOV_STK_ART_EXISTENCIA, MOV_STK_ART_QUANTIDADE, IFNULL(IFNULL(C.FAT_REFERENCIA, F.FAT_REFERENCIA), MOV_REFERENCIA) AS MOV_DOCUMENT_FROM, MOV_STK_ULTIMO_PRECO_CUSTO, UNI_SIGLA, DS.DOC_DESCRICAO, AF.ARM_DESCRICAO AS WAREHOUSE_FROM, AT.ARM_DESCRICAO AS WAREHOUSE_TO, 
IFNULL(MOV_TRANSFERENCIA_ID, -1) AS MOV_TRANSFERENCIA_ID
   FROM GER_ARTIGO P
INNER JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = ART_CODIGO
INNER JOIN STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS ON MOV_STK_ART_CODIGO_ARTIGO = STOCK_CODIGO_ARTIGO
INNER JOIN STOCk_MOVIMENTO_DIRECTOS ON MOV_CODIGO = MOV_STK_ART_CODIGO_MOVIMENTO
INNER JOIN COM_DOCUMENTO DS ON DS.DOC_CODIGO = MOV_CODIGO_DOCUMENTO
INNER JOIN GER_ARMAZEM A ON A.ARM_CODIGO = MOV_CODIGO_ARMAZEM
INNER JOIN COM_SERIE_DOCUMENTACAO SS ON SER_CODIGO = MOV_SERIE_ID 
INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
