IF(FILIAL > 0) THEN
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO_FILIAL = ",FILIAL); 
END IF;
  /*
  IF(DATA_INI IS NOT NULL  AND DATA_TERM IS NOT NULL) THEN
  	SET vCONSULTA =CONCAT(vCONSULTA," AND  MOV_STK_ART_CREATED_DATE BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "'");
	ELSE IF(DATA_INI IS NOT NULL AND DATA_TERM IS NULL ) 	THEN
	  SET vCONSULTA =CONCAT(vCONSULTA," AND MOV_STK_ART_CREATED_DATE >='",DATA_INI, "'");
	ELSE IF(DATA_INI IS NULL  AND DATA_TERM IS NOT NULL) 	THEN
	  SET vCONSULTA =CONCAT(vCONSULTA," AND MOV_STK_ART_CREATED_DATE <= '",DATA_TERM, "'");
	END IF;
	END IF;
	END IF; 
	*/



#SELECT vCONSULTA;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY MOV_STK_ART_CREATED_DATE ");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
 

END
