DROP PROCEDURE IF EXISTS `stp_STOCK_INVENTORY_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_INVENTORY_ADICIONAR`(IN `INVENTORY_ID` int,IN `REFERENCE` varchar(120),IN `WAREHOUSE_ID` int,IN `INVENTORY_DATE` datetime,IN `INVENTORY_LAUCH` date,IN `EMPLOYEE_ID` int,IN `COMPANY_ID` int,IN `UTILIZADOR` varchar(20), pSTATUS VARCHAR(1), OUT LSTID INT)
BEGIN
	#Routine body goes here...
SELECT GAR_CODIGO INTO EMPLOYEE_ID FROM REST_FUNCIONARIOS WHERE GAR_CODIGO_ENTIDADE = EMPLOYEE_ID;
IF(INVENTORY_ID <=0) THEN

SELECT IFNULL(INV_CODIGO, -1) INTO INVENTORY_ID FROM COM_STOCK_INVENTARIO WHERE INV_DESCRICAO=REFERENCE;

IF(INVENTORY_ID <=0 ) THEN
   INSERT INTO COM_STOCK_INVENTARIO VALUES(DEFAULT, WAREHOUSE_ID, INVENTORY_DATE, REFERENCE, INVENTORY_LAUCH, 
   EMPLOYEE_ID, pSTATUS, COMPANY_ID, UTILIZADOR, NOW(), UTILIZADOR, NOW());
   SELECT LAST_INSERT_ID() INTO INVENTORY_ID; 
END IF;
ELSE
 UPDATE COM_STOCK_INVENTARIO SET
 INV_DATA_INICIO = INVENTORY_DATE,
 INV_DATA_ENCERRAMENTO = INVENTORY_LAUCH,
 INV_RESPONSAVEL = EMPLOYEE_ID,
 INV_STATUS = pSTATUS,
 INV_UPDATED_BY = UTILIZADOR,
 INV_UPDATED_DATE = NOW(),
 INV_CODIGO_ARMAZEM = WAREHOUSE_ID
 WHERE INV_CODIGO = INVENTORY_ID;
END IF;

SELECT INVENTORY_ID INTO LSTID;
END
;;
delimiter ;

