-- ----------------------------
-- Procedure structure for stp_STOCK_INVENTORY_ITEM_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_INVENTORY_ITEM_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_INVENTORY_ITEM_ADICIONAR`(IN `INVENTORY_ID` int,IN `PRODUCT_ID` int,IN `EXISTENCIA` decimal(18,5),IN `CONTAGEM` decimal(18,5),IN `ACERTO` decimal(18,5), LOTE_ID INT, SIZE_ID INT, SERIAL_ID INT, PRECO_CUSTO decimal(18,5), PRECO_VENDA decimal(18,5))
BEGIN
	#Routine body goes here... 
DECLARE SITUACAO VARCHAR(2) DEFAULT "NC";

DELETE FROM COM_STOCK_INVENTARIO_ARTIGOS WHERE INV_CODIGO_INVENTARIO = INVENTORY_ID AND INV_CODIGO_ARTIGO = PRODUCT_ID; 

SELECT INV_STATUS INTO SITUACAO FROM COM_STOCK_INVENTARIO WHERE INV_CODIGO = INVENTORY_ID;

IF(SITUACAO ="F") THEN
  
  IF(CONTAGEM >=0) THEN
    SET SITUACAO ="IN";
  ELSE
    SET SITUACAO = "NC";
  END IF;
ELSE IF(SITUACAO ="C") THEN

  IF(CONTAGEM >=0) THEN
    SET SITUACAO ="CT";
  ELSE
    SET SITUACAO = "NC";
  END IF; 
END IF;
END IF;

INSERT INTO COM_STOCK_INVENTARIO_ARTIGOS VALUES(INVENTORY_ID, PRODUCT_ID, EXISTENCIA, CONTAGEM, ACERTO, SITUACAO, LOTE_ID, SIZE_ID, SERIAL_ID/*, PRECO_CUSTO, PRECO_VENDA*/);

END
;;
