-- ----------------------------
-- Procedure structure for stp_STOCK_INVENTORY_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_INVENTORY_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_INVENTORY_OBTERPORFILTRO`(IN `REFERENCIA` varchar(120), IN ESTADO VARCHAR(2), PERIODO DATE, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

#SET @REFERENCE:= CONCAT('%',LOWER(TRIM(REFERENCIA)),'%'); 
SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR; 

SET vCONSULTA = CONCAT("
SELECT INV_CODIGO, INV_DATA_INICIO, INV_DESCRICAO, ARM_DESCRICAO, ENT_NOME_COMPLETO, INV_STATUS, INV_RESPONSAVEL 
FROM COM_STOCK_INVENTARIO 
INNER JOIN GER_ARMAZEM ON ARM_CODIGO = INV_CODIGO_ARMAZEM
LEFT JOIN REST_FUNCIONARIOS ON GAR_CODIGO = INV_RESPONSAVEL
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE
WHERE INV_DESCRICAO LIKE '%",REFERENCIA,"%'");

IF(ESTADO!="-1") THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND INV_STATUS='",ESTADO,"'"); 
END IF;

IF(PERIODO IS NOT NULL) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND DATE(INV_DATA_INICIO) >=",PERIODO); 
END IF;


IF(WAREHOUSE_ID > 0) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND INV_CODIGO_ARMAZEM = ",WAREHOUSE_ID); 
ELSE
  SET vCONSULTA = CONCAT(vCONSULTA," AND INV_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
  WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))"); 
END IF;

#SELECT vCONSULTA;

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_STOCK_INVENTORY_OBTERPRODUCTS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_INVENTORY_OBTERPRODUCTS`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_INVENTORY_OBTERPRODUCTS`(IN `INVENTORY_ID` INT)
BEGIN
	#Routine body goes here...

SELECT INV_CODIGO,
INV_CODIGO_ARMAZEM,
INV_DATA_INICIO,
INV_DESCRICAO,
INV_DATA_ENCERRAMENTO,
INV_RESPONSAVEL,
ENT_NOME_COMPLETO,
INV_STATUS,
INV_CREATED_DATE,
ART_CODIGO, ART_CODIGO_BARRAS, ART_REFERENCIA, ART_DESIGNACAO, CAT_DESCRICAO, IA.INV_EXISTENCIA, ART_PRECO_CUSTO, ART_PRECO_VENDA,
STOCK_CODIGO_ARMAZEM, ART_IMAGEM, IA.INV_CONTAGEM, IA.INV_DIFERENCA, UPPER(UNI_SIGLA) AS UNI_SIGLA,
IFNULL(INV_LOTE_ID, -1) AS INV_LOTE_ID, IFNULL(INV_DIMENSAO_ID, -1) AS INV_DIMENSAO_ID, IFNULL(INV_SERIAL_ID, -1) AS INV_SERIAL_ID, 
LOT_REFERENCIA, DIM_REFERENCIA, SERIAL_SERIAL_NUMBER, 0 as INV_PRECO_CUSTO, 0 as INV_PRECO_VENDA

FROM GER_ARTIGO 
INNER JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = ART_CODIGO
INNER JOIN COM_STOCK_INVENTARIO_ARTIGOS IA ON IA.INV_CODIGO_ARTIGO = ART_CODIGO
INNER JOIN COM_STOCK_INVENTARIO ON INV_CODIGO = IA.INV_CODIGO_INVENTARIO
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
LEFT JOIN REST_FUNCIONARIOS F ON F.GAR_CODIGO = INV_RESPONSAVEL
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE 
INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
LEFT JOIN GER_ARTIGO_LOTE ON LOT_CODIGO = INV_LOTE_ID
LEFT JOIN GER_ARTIGO_DIMENSOES ON DIM_CODIGO = INV_DIMENSAO_ID
LEFT JOIN GER_ARTIGO_SERIAL_NUMBER ON SERIAL_CODIGO = INV_SERIAL_ID
WHERE  INV_CODIGO = INVENTORY_ID;
END
;;
