delimiter ;;
CREATE PROCEDURE `stp_STOCK_MOVIMENTO_DIRECTOS_ADICIONAR`(IN `CODIGO` int,IN `DOCUMENTO` int,IN `REFERENCIA` varchar(20),IN OPERACAO int,IN `NUMERACAO` int,IN `DATA_STOCK` date,IN `LANCAMENTO` datetime,IN `ARMAZEM_FROM` int,IN `ARMAZEM_TO` int,IN `USER_UPDATE` varchar(20),IN `USER_CREATE` varchar(20),IN `UPDATE_DATE` datetime,IN SERIE int, in FUNCIONARIO INT, DOCUMENT_ID INT,  ENTITY_ID INT, ARMAZEM_ID INT, TRANSFER_ID INT, DOCUMENT_FROM INT, IN MOTIVO_ID INT,  OUT LSTID int)
BEGIN
  #Routine body goes here...
DECLARE DOCUMENT_SIMBOL VARCHAR(2) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE PREFIXO VARCHAR(12);

IF(FUNCIONARIO = -1) THEN
SET FUNCIONARIO = NULL;
END IF;

IF(DOCUMENT_ID = -1) THEN
SET DOCUMENT_ID = NULL;
END IF;

IF(ENTITY_ID = -1) THEN
SET ENTITY_ID = NULL;
END IF;

IF(ARMAZEM_ID = -1) THEN
SET ARMAZEM_ID = ARMAZEM_FROM;
END IF;

IF(TRANSFER_ID = -1) THEN
SET TRANSFER_ID = NULL;
END IF;

IF(DOCUMENT_FROM = -1) THEN
SET DOCUMENT_FROM = NULL;
END IF;

IF(CODIGO <=0) THEN

SELECT IFNULL(MOV_CODIGO,0) INTO CODIGO FROM STOCK_MOVIMENTO_DIRECTOS WHERE (MOV_REFERENCIA IS NULL OR MOV_REFERENCIA ="");
DELETE FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS WHERE MOV_STK_ART_CODIGO_MOVIMENTO=CODIGO;
DELETE  FROM STOCK_MOVIMENTO_DIRECTOS WHERE MOV_CODIGO =CODIGO;
SET CODIGO = -1;

END IF ;

IF(CODIGO<=0)THEN
IF(OPERACAO=1 OR OPERACAO=2) THEN
INSERT INTO STOCK_MOVIMENTO_DIRECTOS
(
  MOV_CODIGO_DOCUMENTO, 
  MOV_REFERENCIA,
  MOV_NUMERACAO, 
  MOV_DATA_STOCK,
  MOV_DATA_LANCAMENTO,
  MOV_CODIGO_ARMAZEM_FROM,
  MOV_CODIGO_ARMAZEM_TO,
  MOV_USER_CREATE,
  MOV_FUNCIONARIO,
  MOV_DOCUMENT_ID,
  MOV_ENTITY_ID,
  MOV_CODIGO_ARMAZEM, 
  MOV_TRANSFERENCIA_ID,
  MOV_SERIE_ID,
  MOV_DOCUMENT_FROM,
  MOV_JUSTIFICATIVA	
)
VALUES
(
  DOCUMENTO,
  REFERENCIA,
  NUMERACAO,
  DATA_STOCK,
  LANCAMENTO,
  ARMAZEM_FROM,
  ARMAZEM_TO,
  USER_CREATE,
  FUNCIONARIO,
  DOCUMENT_ID,
  ENTITY_ID,
  ARMAZEM_ID,
  TRANSFER_ID,
  SERIE,
  DOCUMENT_FROM,
	MOTIVO_ID
);
SELECT LAST_INSERT_ID() INTO CODIGO;
SELECT MOV_REFERENCIA INTO REFERENCIA FROM STOCK_MOVIMENTO_DIRECTOS WHERE MOV_CODIGO = CODIGO;
IF(REFERENCIA IS NULL OR REFERENCIA ='') THEN
   SELECT DOC_SIGLA INTO DOCUMENT_SIMBOL FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
   SELECT SER_NUMERACAO, SER_ANO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
         SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL,"/",ANO_FATURACAO, "-",PREFIXO);

     UPDATE STOCK_MOVIMENTO_DIRECTOS SET
     MOV_NUMERACAO = DOCUMENT_NUMBER,
     MOV_REFERENCIA = REFERENCIA
     WHERE MOV_CODIGO = CODIGO;

    UPDATE COM_SERIE_DOCUMENTACAO SET
    SER_NUMERACAO = SER_NUMERACAO + 1
    WHERE SER_CODIGO = SERIE;
END IF;
ELSEIF(OPERACAO=3) THEN
INSERT INTO STOCK_MOVIMENTO_DIRECTOS
(
  MOV_CODIGO_DOCUMENTO,
  MOV_REFERENCIA,
  MOV_NUMERACAO,
  MOV_DATA_STOCK,
  MOV_DATA_LANCAMENTO,
  MOV_CODIGO_ARMAZEM_FROM,
  MOV_CODIGO_ARMAZEM_TO,
  MOV_USER_CREATE,
  MOV_FUNCIONARIO,
  MOV_DOCUMENT_ID,
  MOV_ENTITY_ID,
  MOV_CODIGO_ARMAZEM,
  MOV_SERIE_ID,
  MOV_DOCUMENT_FROM,
  MOV_JUSTIFICATIVA	
)
VALUES
(
  DOCUMENTO,
  REFERENCIA,
  NUMERACAO,
  DATA_STOCK,
  LANCAMENTO,
  ARMAZEM_FROM,
  ARMAZEM_TO,
  USER_CREATE,
  FUNCIONARIO,
  DOCUMENT_ID,
  ENTITY_ID,
  ARMAZEM_ID,
  SERIE,
  DOCUMENT_FROM,
	MOTIVO_ID
);
SELECT LAST_INSERT_ID() INTO CODIGO;
SELECT MOV_REFERENCIA INTO REFERENCIA FROM STOCK_MOVIMENTO_DIRECTOS WHERE MOV_CODIGO = CODIGO;
IF(REFERENCIA IS NULL OR REFERENCIA ='') THEN
   SELECT DOC_SIGLA INTO DOCUMENT_SIMBOL FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
   SELECT SER_NUMERACAO, SER_ANO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
         SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL,"/",ANO_FATURACAO, "-",PREFIXO);

     UPDATE STOCK_MOVIMENTO_DIRECTOS SET
     MOV_NUMERACAO = DOCUMENT_NUMBER,
     MOV_REFERENCIA = REFERENCIA
     WHERE MOV_CODIGO = CODIGO;

    UPDATE COM_SERIE_DOCUMENTACAO SET
    SER_NUMERACAO = SER_NUMERACAO + 1
    WHERE SER_CODIGO = SERIE;
END IF;
END IF;
ELSE
DELETE FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS WHERE MOV_STK_ART_CODIGO_MOVIMENTO=CODIGO;
IF(OPERACAO=1 OR OPERACAO=2) THEN
UPDATE STOCK_MOVIMENTO_DIRECTOS SET 
  MOV_CODIGO_DOCUMENTO=DOCUMENTO,
  MOV_REFERENCIA=REFERENCIA,
  MOV_NUMERACAO=NUMERACAO,
  MOV_DATA_STOCK=DATA_STOCK,
  MOV_DATA_LANCAMENTO=LANCAMENTO,
  MOV_CODIGO_ARMAZEM_FROM=ARMAZEM_FROM,
  MOV_CODIGO_ARMAZEM_TO = ARMAZEM_TO,
  MOV_USER_UPDATE=USER_UPDATE,
  MOV_USER_CREATE=USER_CREATE,
  MOV_UPDATE_DATE=UPDATE_DATE,
  MOV_FUNCIONARIO = FUNCIONARIO,
  MOV_DOCUMENT_ID = DOCUMENT_ID,
  MOV_ENTITY_ID = ENTITY_ID,
  MOV_CODIGO_ARMAZEM =ARMAZEM_ID, 
  MOV_TRANSFERENCIA_ID = TRANSFER_ID,
  MOV_SERIE_ID = SERIE,
  MOV_DOCUMENT_FROM = DOCUMENT_FROM,
	MOV_JUSTIFICATIVA = MOTIVO_ID
WHERE MOV_CODIGO=CODIGO;
ELSE
IF(OPERACAO=3) THEN
UPDATE STOCK_MOVIMENTO_DIRECTOS SET 
  MOV_CODIGO_DOCUMENTO=DOCUMENTO,
  MOV_REFERENCIA=REFERENCIA,
  MOV_NUMERACAO=NUMERACAO,
  MOV_DATA_STOCK=DATA_STOCK,
  MOV_DATA_LANCAMENTO=LANCAMENTO,
  MOV_CODIGO_ARMAZEM_FROM=ARMAZEM_FROM,
  MOV_CODIGO_ARMAZEM_TO=ARMAZEM_TO,
  MOV_USER_UPDATE=USER_UPDATE,
  MOV_USER_CREATE=USER_CREATE,
  MOV_UPDATE_DATE=UPDATE_DATE,
  MOV_FUNCIONARIO = FUNCIONARIO,
  MOV_DOCUMENT_ID = DOCUMENT_ID,
  MOV_ENTITY_ID = ENTITY_ID,
  MOV_CODIGO_ARMAZEM =ARMAZEM_ID,
  MOV_SERIE_ID = SERIE,
  MOV_DOCUMENT_FROM = DOCUMENT_FROM,
  MOV_JUSTIFICATIVA = MOTIVO_ID	
WHERE MOV_CODIGO=CODIGO;
END IF;
END IF;
END IF;
SELECT CODIGO INTO LSTID;
END
;;
delimiter ;

