-- ----------------------------
-- Procedure structure for stp_STOCK_MOVIMENTO_DIRECTO_ARTIGOS_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_MOVIMENTO_DIRECTO_ARTIGOS_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_MOVIMENTO_DIRECTO_ARTIGOS_OBTERPORFILTRO`(IN `MOVIMENT_ID` int, DATA_INI DATE, DATA_TERM DATE)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;
SET vCONSULTA = CONCAT("
SELECT DISTINCT ART_CODIGO, ART_REFERENCIA, ART_DESIGNACAO, MOV_STK_ART_QUANTIDADE, UNI_SIGLA, F.ARM_DESCRICAO AS ARMAZEM_ORIGEM, 
D.ARM_DESCRICAO AS ARMAZEM_DESTINO, LOT_REFERENCIA, IFNULL(MOV_STK_ART_PRECO_UNITARIO, 0) AS MOV_STK_ART_PRECO_UNITARIO, 
IFNULL(MOV_STK_ULTIMO_PRECO_CUSTO , 0) AS MOV_STK_ULTIMO_PRECO_CUSTO, CASE WHEN MOV_DOCUMENT_FROM > 0 THEN FAT_REFERENCIA
ELSE MOV_REFERENCIA END AS DOCUMENT_REFERENCE, MOV_STK_ART_DATA_MOVIMENTO, MOV_STK_STATUS, MOV_STK_ULTIMO_PRECO_CUSTO 
FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS
INNER JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = MOV_STK_ART_CODIGO_ARTIGO
INNER JOIN GER_ARTIGO ON ART_CODIGO = STOCK_CODIGO_ARTIGO
INNER JOIN GER_ARMAZEM F ON F.ARM_CODIGO = MOV_STK_ART_CODIGO_ARMAZEM_FROM
LEFT JOIN GER_ARMAZEM D ON D.ARM_CODIGO = MOV_STK_ART_CODIGO_ARMAZEM_TO
INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
LEFT JOIN GER_ARTIGO_LOTE ON LOT_CODIGO = MOV_STK_LOTE_ID 
INNER JOIN STOCK_MOVIMENTO_DIRECTOS ON MOV_CODIGO = MOV_STK_ART_CODIGO_MOVIMENTO
LEFT JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_DOCUMENT_ID
WHERE (MOV_STK_STATUS IS NULL OR MOV_STK_STATUS=1) ");

IF(MOVIMENT_ID > 0) THEN

SET vCONSULTA = CONCAT(vCONSULTA," AND MOV_STK_ART_CODIGO_MOVIMENTO = ", MOVIMENT_ID);

END IF;

IF(DATA_INI IS NOT NULL  AND DATA_TERM IS NOT NULL) THEN
	SET vCONSULTA =CONCAT(vCONSULTA," AND  MOV_STK_ART_DATA_MOVIMENTO BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "'");
	ELSE IF(DATA_INI IS NOT NULL AND DATA_TERM IS NULL )
	THEN
	SET vCONSULTA =CONCAT(vCONSULTA," AND MOV_STK_ART_DATA_MOVIMENTO >='",DATA_INI, "'");
	ELSE IF(DATA_INI IS NULL  AND DATA_TERM IS NOT NULL)
	THEN
	SET vCONSULTA =CONCAT(vCONSULTA," AND MOV_STK_ART_DATA_MOVIMENTO <= '",DATA_TERM, "'");
	END IF;
	END IF;
	END IF; 

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY MOV_CODIGO, MOV_STK_ART_DATA_MOVIMENTO, MOV_STK_ART_CREATED_BY  ");

#SELECT vCONSULTA;
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;



END
;;
delimiter ;

