-- Procedure structure for stp_STOCK_MOVIMENTO_DIRECTO_ARTIGO_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_MOVIMENTO_DIRECTO_ARTIGO_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_MOVIMENTO_DIRECTO_ARTIGO_ADICIONAR`(IN `MOVIMENTO` int,IN `ARTIGO` int,IN `DATA_MOVIMENTO` datetime,IN `EXISTENCIA` decimal(18,2), IN ARMAZEM_ORIGEM INT, ARMAZEM_DESTINO INT, IN `QUANTIDADE` decimal(18,5),IN QTD_FINAL decimal(18,5),IN `PRECO_UNITARIO` decimal(18,5), IN TIPO int,  IN `PRECO_CUSTO` decimal(18,5), IN `VALOR_PVP` decimal(18,5), IN `VALOR_PC` decimal(18,5), IN UTILIZADOR VARCHAR(20), STATUS INT, FILIAL INT, LOTE_ID INT, SERIAL_NUMBER_ID INT, COMPOSE_ID  INT, SIZE_ID INT, DATA_CONTAGEM DATETIME)
BEGIN
  #Routine body goes here...
DECLARE MOVIMENTA INT DEFAULT 0;
DECLARE CUSTO_MEDIO DECIMAL(18,5) DEFAULT 0;
DECLARE SALES_PRICE DECIMAL(18,5) DEFAULT 0;
DECLARE PURCHAGE_PRICE DECIMAL(18,5) DEFAULT 0;

SELECT IFNULL(ART_PRECO_CUSTO,0), IFNULL(ART_PCM_CALCULADO,0), IFNULL(ART_PRECO_VENDA,0) INTO PURCHAGE_PRICE, CUSTO_MEDIO, PURCHAGE_PRICE  FROM GER_ARTIGO WHERE ART_CODIGO=ARTIGO;
IF(TIPO=1 OR TIPO =2) THEN
SELECT ART_MOVIMENTA_STOCK INTO MOVIMENTA  FROM GER_ARTIGO WHERE ART_CODIGO = ARTIGO;
/*
IF(QTD_FINAL!=(EXISTENCIA - QUANTIDADE)) THEN
 SET QTD_FINAL = EXISTENCIA - QUANTIDADE;
END IF;
*/
IF(MOVIMENTA=1) THEN


IF(NOT EXISTS(SELECT * FROM COM_STOCK WHERE STOCK_CODIGO_ARMAZEM=ARMAZEM_ORIGEM AND STOCK_CODIGO_ARTIGO=ARTIGO))THEN
   INSERT INTO COM_STOCK VALUES(ARMAZEM_ORIGEM, ARTIGO, 0, 0, 1, 99, 0, NULL, NULL, 0, 0, 0, 0, 0, 0, NULL, NULL, UTILIZADOR, NOW(), UTILIZADOR, NOW()); 
END IF;

IF(NOT EXISTS(SELECT * FROM COM_STOCK WHERE STOCK_CODIGO_ARMAZEM=ARMAZEM_DESTINO AND STOCK_CODIGO_ARTIGO=ARTIGO))THEN
   INSERT INTO COM_STOCK VALUES(ARMAZEM_DESTINO, ARTIGO, 0, 0, 1, 99, 0, NULL, NULL, 0, 0, 0, 0, 0, 0, NULL, NULL, UTILIZADOR, NOW(), UTILIZADOR, NOW()); 
END IF;
 
 IF(VALOR_PC <=0) THEN 
   SET VALOR_PC = CUSTO_MEDIO;
  IF(VALOR_PC > 0) THEN
   SET VALOR_PC = (VALOR_PC + IFNULL(PRECO_CUSTO, PURCHAGE_PRICE))/2; 
