-- ----------------------------
-- Procedure structure for stp_STOCK_MOVIMENTO_DIRECTO_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_STOCK_MOVIMENTO_DIRECTO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_STOCK_MOVIMENTO_DIRECTO_OBTERPORFILTRO`(IN REFERENCIA varchar(30),IN INICIO date, IN TERMINO date, IN ARMAZEM int, IN INVOICE_ID INT)
BEGIN
  #Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA=CONCAT("
SELECT
	M.MOV_CODIGO AS CODIGO,
  D.DOC_DESCRICAO AS MOVIMENTO, M.MOV_REFERENCIA AS DOCUMENTO ,
  Z.ARM_DESCRICAO AS ARMAZEM_FROM,
  W.ARM_DESCRICAO AS ARMAZEM_TO,
  M.MOV_DATA_LANCAMENTO, M.MOV_USER_CREATE AS UTILIZADOR
FROM STOCK_MOVIMENTO_DIRECTOS M
LEFT JOIN COM_DOCUMENTO D ON D.DOC_CODIGO=M.MOV_CODIGO_DOCUMENTO
LEFT JOIN GER_ARMAZEM Z ON Z.ARM_CODIGO=M.MOV_CODIGO_ARMAZEM_FROM
LEFT JOIN GER_ARMAZEM W ON W.ARM_CODIGO=M.MOV_CODIGO_ARMAZEM_TO 
WHERE (MOV_DELETED IS NULL OR MOV_DELETED != 1) AND M.MOV_DOCUMENT_FROM IS NULL
");
IF(INVOICE_ID > 0) THEN
  SET vCONSULTA = CONCAT(vCONSULTA, " AND MOV_DOCUMENT_ID = ", INVOICE_ID);
ELSE
  SET vCONSULTA = CONCAT(vCONSULTA, " AND M.MOV_REFERENCIA LIKE '%",REFERENCIA,"%'");
	IF(INICIO <>'' AND TERMINO='') THEN
	SET vCONSULTA=CONCAT(vCONSULTA,  " AND MOV_DATA_STOCK BETWEEN '",INICIO,"' AND '",TERMINO,"'");
	END IF;
	IF (ARMAZEM>0)THEN
	SET vCONSULTA=CONCAT(vCONSULTA," AND MOV_CODIGO_ARMAZEM=",ARMAZEM);
	ELSE 
	SET vCONSULTA=CONCAT(vCONSULTA," AND MOV_TRANSFERENCIA_ID IS NULL  ");
	END IF;
END IF;

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
