delimiter ;;
CREATE PROCEDURE `stp_sys_retifica_notas_credito`(IN CODIGO INT)
BEGIN
	#Routine body goes here...
	 
	DECLARE NUMERO_DOC_ORIGEM INT DEFAULT 0;
	DECLARE ORIGINAL_DOC_VALUE DECIMAL(18,5);
	DECLARE VALOR_TOTAL DECIMAL(18,5);
	
	 
 
	
		SELECT FAT_NUMERO_DOC_ORIGEM, FAT_VALOR_TOTAL INTO NUMERO_DOC_ORIGEM, VALOR_TOTAL 
		FROM COM_FATURA_CLIENTE 
		INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
	  INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
	  WHERE DOC_FORMATO = "INVOICE_D" AND FAT_CODIGO = CODIGO;
	 
	 
	 SELECT IFNULL(FAT_VALOR_TOTAL, 0) INTO ORIGINAL_DOC_VALUE  FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM
	 AND FAT_STATUS_DOCUMENTO!=7;
			
			IF(ORIGINAL_DOC_VALUE > 0) THEN
			
			  IF(ORIGINAL_DOC_VALUE = VALOR_TOTAL) THEN
			     CALL stp_COM_FATURA_CLIENTE_EXCLUIR(NUMERO_DOC_ORIGEM, "administrador", "erro de lançamento");
				   UPDATE COM_FATURA_CLIENTE SET 
					 FAT_RECTIFIED = 0,
					 FAT_STATUS = 0,
					 FAT_STATUS_DOCUMENTO = 7,
					 FAT_RECTIFIER_DOCUMENT_ID = CODIGO
					 WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM; 
				ELSE
					 UPDATE COM_FATURA_CLIENTE SET 
					 FAT_RECTIFIED = 1,
					 FAT_RECTIFIER_DOCUMENT_ID = CODIGO,
					 FAT_CUSTOMER_CREDIT = IFNULL(FAT_CUSTOMER_CREDIT, 0) + VALOR_TOTAL
					 WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM;
				END IF; 
			
			END IF; 
END
;;
